function poll() {
    $.ajax({
        url: "/users_admin/" + "30", // Modify this line
        type: "GET",
        data: {
            after_id: mostRecentMessageId,
        },
        success: function(data) {
            // Insert the new HTML at the end of the messages div
            $('#messages').append(data.html);

            // Update the most recent message ID
            mostRecentMessageId = data.html ? data.html.match(/data-id="(\d+)"/)[1] : mostRecentMessageId;
        },
        dataType: "json",
        complete: function() {
            // Schedule the next request when the current one's complete
            setTimeout(poll, 5000); // 5 seconds
        },
        timeout: 30000 // 30 seconds
    });
}

$(document).ready(function() {
    poll();
});
